import { useEffect, useState } from "react"
import React  from 'react'
import './style.css'
import WeatherDetails from "./weatherDetails";

function Search() {
    const [searchTerm, setSearchTerm] = useState("mumbai");
    const getWeatherInfo = async() =>{
      try{
        let url = `https://api.openweathermap.org/data/2.5/weather?q=${searchTerm}&appid=c6f1427be983e8c781ce7c99a11f6d02`

        let res = await fetch(url);
        let data = await res.json();
        console.log(data);
      }catch(error){
        console.log(error);
      }


    }
       
    useEffect(() => {
      getWeatherInfo()
    },[])
  return (
    <>
    <div className='wrap'>
        <div className='search'>
            <input 
            type='search' 
            placeholder='Type city name...' 
            id='search' 
            value = {searchTerm} 
            onChange={(e) => setSearchTerm(e.target.value)}
            />
        </div>
        <button className='searchButton' onClick={getWeatherInfo}>Search</button>
    </div>
    <WeatherDetails />
    </>
  )
}
export default Search;
